package com.bulgogi.marblewars.manager;import org.andengine.engine.Engine;import org.andengine.opengl.texture.atlas.bitmap.BitmapTextureAtlasTextureRegionFactory;import android.content.Context;import com.bulgogi.marblewars.game.SplashLoader;public class ResourceManager {	private SplashLoader splashLoader;	private volatile static ResourceManager instance;		private ResourceManager() {		splashLoader = new SplashLoader();	}	public static ResourceManager getInstance() {		if (instance == null) {			synchronized (ResourceManager.class) {				if (instance == null) {					instance = new ResourceManager();				}			}		}		return instance;	}		public SplashLoader getSplash() {		return splashLoader;	}}